function Message() {
  let message = [
    {
      content: "Hello ",
      author: "admin",
      side: "right",
    },
    {
      content: "Hi ",
      author: "Ali",
      side: "left",
    },
    {
      content: "How are you ",
      author: "admin",
      side: "right",
    },
    {
      content: "i am fine  ",
      author: "Ali",
      side: "left",
    },
  ];
  {
    return (
      <div className="flex">
        <div className="container">
          {message.map((message) => {
            return (
              <div
                className={`row ${
                  message.side === "right" ? "	justify-content: flex-end;" : ""
                } `}
              >
                <div className="col-3">
                  <div className="left ">
                    <p>{message.content}</p>
                    <span>{message.author}</span>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    );
  }
}

export default Message;
