function Message() {
  let message = [
    {
      content: "Hello ",
      author: "admin",
      side: "",
    },
    {
      content: "Hi ",
      author: "Ali",
      side: "left",
    },
    {
      content: "How are you ",
      author: "admin",
      side: "right", // установите правую сторону для третьего блока
    },
    {
      content: "i am fine  ",
      author: "Ali",
      side: "left",
    },
  ];

  return (
    <div className="flex">
      <div className="container">
        {message.map((message, index) => {
          return (
            <div className="row justify-content-end">
              <div className="col-3">
                <div className="left">
                  <p>{message.content}</p>
                  <span>{message.author}</span>
                </div>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default Message;
